cardinalidade = [[], [], []]
for k in range(3):
    for i in range(20):
        soma = 0
        for j in range(arestas[k].__len__()):
            if(arestas[k][j][0] == i or arestas[k][j][1] == i):
                soma +=1
        cardinalidade[k].append(soma)
def vizinhosECard(a, grafo):
    vizinhos = []
    cardVizinhos = []
    for j in range(arestas[grafo].__len__()):
        if(arestas[grafo][j][0] == a):
            vizinhos.append(arestas[grafo][j][1])
        elif(arestas[grafo][j][1] == a):
            vizinhos.append(arestas[grafo][j][0])
    for i in range(vizinhos.__len__()):
        cardVizinhos.append(cardinalidade[grafo][vizinhos[i]])
    cardVizinhos.sort(reverse=False)
    vizinhos.sort(reverse=False)
    print(f"grafo {grafo}")
    print(vizinhos, vizinhos.__len__())
    print(cardVizinhos, cardVizinhos.__len__())

